import {format} from "/date-fns";

export default function Post({title,summary,cover,content,createdAt}){

    return(
        <div className="post">
            <div className="image">
                <img src="https://miro.medium.com/v2/resize:fit:828/format:webp/0*W3gAMMuPolMsx6MQ.jpeg" alt="image"></img>
            </div>
            
            <div className="texts">
                <h2>{title}</h2>

                <p className="info">
                <a className="author">Krishna Sree</a>
                <time>{format(new Date(createdAt))}</time>
                </p>

                <p className="summary">{summary}</p>
            </div>
        </div>
    );
}
